// reducer contains 3: feature key, initial state, reducer

import * as pizzaActions from './pizzaHutAction';

export const pizzaHutFeatureKey = 'pizzaHut'; // Corrected the spelling of 'feature'

const initialState = {
  count: 100
};

export const reducer = (state = initialState, action) => {
  switch (action.type) {
    case pizzaActions.BUY_PIZZA:
      return {
        count: state.count - 1 > 0 ? state.count - 1 : 0
      };
    default:
      return state;
  }
};
