import React from "react";
import { useSelector, useDispatch } from "react-redux";
import * as pizzaReducer from '../pizzahut/pizzaHut.reducer';
import * as pizzaHutAction from '../pizzahut/pizzaHutAction';

const PizzaHut = () => {
    const dispatch = useDispatch(); // Get the dispatch function from react-redux

    let pizzaInfo = useSelector((state) => {
        return state[pizzaReducer.pizzaHutFeatureKey];
    });

    let clickBuyPizza = () => {
        // Dispatch the BUY_PIZZA action when the button is clicked
        dispatch(pizzaHutAction.buyPizza());
    };

    return (
        <React.Fragment>
            {/* ... other JSX code ... */}
            <button className="btn btn-success btn-sm" onClick={clickBuyPizza}>
                Buy Pizza!
            </button>
            {/* ... other JSX code ... */}
        </React.Fragment>
    );
};

export default PizzaHut;
