// Uncomment the following line to import React
import React from "react";
//import { ReactDOM } from "react";
import { useDispatch, useSelector } from "react-redux";
import * as pizzaReducer from '../pizzahut/pizzaHut.reducer';
import * as pizzaHutAction from '../pizzahut/pizzaHutAction';

const PizzaHut = () => { 
    let dispatch = useDispatch();
    //get pizza info rom redux store
    let pizzaInfo = useSelector((state)=>{
        return state[pizzaReducer.pizzaHutFeatureKey];
    });

    let clickBuyPizza = () => {
        // setPizzaCount(pizzaCount -1 > 0 ? pizzaCount -1 : 0)    
        dispatch(pizzaHutAction.buyPizza());
      };

    return (
        <React.Fragment>
            <section>
                <div className="container">
                    <div className="row">
                        <div className="col">
                            <p className="h3 text-danger">PizzaHut</p>
                            <p className="lead">This is a new pizza order in the store and explore new life</p>
                        </div>
                    </div>
                </div> 
            </section>
            <section className="p-3">
                <div className="container">
                    <div className="row">
                        <div className="col-md-5">
                            <img src="https://img.freepik.com/free-photo/freshly-italian-pizza-with-mozzarella-cheese-slice-generative-ai_188544-12347.jpg" className="img-fluid" width="400px" height="400px" alt="Pizza" />
                        </div>
                        <div className="col-md-7">
                            <p className="h2">Chicken Pizza</p>
                            <p className="h3"></p>
                            <span className="p-detailes font-wegiht bold text-center">{pizzaInfo.count}</span>
                            <p>This is pizza</p>
                            <button className="btn btn-success btn-sm" onClick={clickBuyPizza}>
                                Buy Pizza!</button>
                        </div>
                    </div>
                </div>
            </section>
        </React.Fragment>
    );
};

export default PizzaHut;
