import React, { useState } from "react";
import { useSelector } from "react-redux";
import * as pizzaReducer from '../pizzahut/pizzaHut.reducer';
import * as pizzaActions from '../pizzahut/pizzaHutAction';
//import React from '../node_modules/mdbootstrap/css/mdb.css';

const PizzaHut = () => { 
    let pizzaInfo = useSelector((state)=>{
        return
    })
    let [pizzaCount,setPizzaCount] = useState(50);

    let clickBuyPizza = () =>{
        setPizzaCount(pizzaCount -1 > 0 ? pizzaCount -1 : 0)
    }
    return (
        <React.Fragment>
            <section>
            <div className="container">
                <div className="row">
                    <div className="col">
                        <p className="h3 text-danger">PizzaHut</p>
                        <p className="lead">this is new pizza order in the store and explore new life</p>
                    </div>
                </div>
            </div> 
            </section>
            <section className="p-3">
                <div className="container">
                    <div className="row">
                        <div className="col-md-5">
                            <img src="https://img.freepik.com/free-photo/freshly-italian-pizza-with-mozzarella-cheese-slice-generative-ai_188544-12347.jpg" className="img-fluid" width="400px" height="400px" alt="Pizza" />
                        </div>
                        <div className="col-md-7">
                            <p className="h2">Chicken Pizza</p>
                            <p className="h3">{pizzaCount}</p>
                            <span className="p-detailes font-wegiht bold text-center"> this is every one like .twesty mongo flover and chees onions eith masala gravy .pickesh to have you enjoy the day!!
                            </span>
                            <button className="btn btn-success btn-sm" onClick={clickBuyPizza}>
                                !Buy Pizza!</button>
                        </div>
                    </div>
                </div>
            </section>
        </React.Fragment>
    );
};

export default PizzaHut;
